<script>
// 使用IIFE（立即执行函数表达式）避免污染全局作用域
(function() {
  const container = document.querySelector('.nova-silent-sky.nova-container');
  if (!container) return;

  // 1. 隐藏空的选项按钮
  const buttons = container.querySelectorAll('.action-btn-silent');
  buttons.forEach(btn => {
    // 检查 data-action 属性是否存在且不为空
    const action = btn.getAttribute('data-action');
    if (!action) {
      btn.style.display = 'none';
    }
  });

  // 2. 隐藏空的Tips面板
  const tipsPanel = container.querySelector('.tips-panel');
  const tipsContent = container.querySelector('.tips-content');
  if (tipsPanel && (!tipsContent || !tipsContent.textContent.trim())) {
    tipsPanel.style.display = 'none';
  }

  // 3. 绑定点击事件 (与之前相同)
  buttons.forEach(btn => {
    btn.addEventListener('click', () => {
      const message = btn.getAttribute('data-action');
      if (typeof triggerSlash === 'function') {
        triggerSlash(`/send ${message}|/trigger`);
      } else {
        console.log('triggerSlash 未定义, 模拟发送: ', message);
      }

      const mainContainer = btn.closest('.nova-container');
      if (mainContainer) {
        mainContainer.classList.add('fading-out');
        setTimeout(() => { mainContainer.style.display = 'none'; }, 260);
      }
    });
  });
})();
</script>
