custom_css:
/* 基于深蓝磨砂主题优化的qq上下暗黑主题 (v5.2 - 内置头像布局) */
/* 颜色变量定义 */
:root {
  --dark-bg-primary: #0a1929;
  --dark-bg-secondary: #132f4c; 
  --dark-bg-user: #1e3a5f;
  --dark-bg-ai: #1a2332;
  --dark-text-secondary: #a6b8cc;
  --dark-accent-primary: #7fdbca;
  --dark-accent-secondary: #5090d3;
  --dark-border-color: rgba(94, 158, 214, 0.15);
  --dark-shadow: rgba(0, 0, 0, 0.3);
}

/* --- 修改: 统一左右布局，头像内置 --- */
/* 将.mes作为整个消息的大框 */
.mes {
    display: flex;
    flex-direction: row; /* 头像和消息水平排列 */
    align-items: flex-start; /* 顶部对齐 */
    gap: 15px; /* 头像和消息文本的间距 */
    padding: 15px 15px 15px 5px; /* 修改：减少左侧内边距，让头像更靠左 */
    border-radius: 16px;
    margin-bottom: 12px; /* 消息间的垂直间距 */
}

/* AI消息样式 */
.mes[is_user=false] {
    background: var(--dark-bg-ai);
    border: 1px solid var(--dark-border-color);
    box-shadow: 0 2px 8px var(--dark-shadow);
}

/* 用户消息样式 (头像同样在左侧) */
.mes[is_user=true] {
    background: var(--dark-bg-user);
    border: 1px solid rgba(80, 144, 211, 0.3);
    box-shadow: 0 2px 12px rgba(80, 144, 211, 0.15);
}

/* 重置原有的.mes_block样式，使其不再是独立的带背景方块 */
.mes_block {
  padding: 0;
  background: none;
  border: none;
  box-shadow: none;
  backdrop-filter: none;
  -webkit-backdrop-filter: none;
  flex-grow: 1; /* 让文本区域占据剩余空间 */
}

/* 按要求移除AI和用户消息的所有渐变装饰线 */
.mes[is_user=false] .mes_block::before,
.mes[is_user=true] .mes_block::before {
  display: none;
}

/* 调整头像容器和头像大小 */
.mes .mesAvatarWrapper {
    padding-bottom: 0;
    flex-shrink: 0; /* 防止头像被挤压 */
}
.mesAvatarWrapper .avatar img,.mesAvatarWrapper .avatar{width:52px;height:52px;}

/* 文字颜色优化 */
.mes_block .mes_text > p {color: var(--dark-text-primary)} 
.mes[is_user=true] .mes_block *{color: var(--dark-text-primary) !important}
.mesIDDisplay,.mes_timer,.tokenCounterDisplay{margin:0 5px; color: var(--dark-text-secondary);opacity: 0.8;}

/* 编辑按钮样式 */
.mes[is_user=true] .mes_edit_buttons *:before{color:#ccc !important}
.swipeRightBlock{right:-8px}
.mes_text{padding-right:0px;padding-bottom:0}

/* 头像样式优化 */
.mesAvatarWrapper .avatar img{
  border:2px solid var(--dark-border-color);
  box-shadow: 0 0 12px rgba(127, 219, 202, 0.3);
  transition: all 0.3s ease;
}
.mes[is_user=true] .mesAvatarWrapper .avatar img{
  box-shadow: 0 0 12px rgba(80, 144, 211, 0.4);
}
.mesAvatarWrapper .avatar img:hover{
  transform: scale(1.05);
  box-shadow: 0 0 16px rgba(127, 219, 202, 0.5);
}

/* --- 思考内容(Thought)样式优化 --- */
.mes_reasoning button {
  background-color: var(--dark-bg-secondary);
  color: var(--dark-text-secondary);
  border: 1px solid var(--dark-border-color);
  border-radius: 8px;
  padding: 6px 12px;
  margin-top: 8px;
  transition: all 0.2s ease;
}
.mes_reasoning button:hover {
  background-color: var(--dark-bg-user);
  border-color: var(--dark-accent-secondary);
  color: var(--dark-text-primary);
}

/* 顶部栏处理 */
#top-bar {
    height: 0px !important;
    background: none !important;
    border: none !important;
    overflow: visible !important;
}
#top-bar .fa-solid, #top-bar .fa-regular {
    color: var(--dark-text-secondary);
}

/* 底部设置栏 */
#top-settings-holder{
  position:absolute;
  bottom:0px !important;
  width:100%;
  background: rgba(10, 25, 41, 0.95);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border-top: 1px solid var(--dark-border-color);
}

/* 面板位置 */
#sheld,.openDrawer{top:0;position:fixed}
#right-nav-panel, #left-nav-panel, #floatingPrompt, #cfgConfig, #logprobsViewer, #movingDivs>div{top:0 !important}

/* 设置项样式 */
#extensions_settings .inline-drawer-toggle.inline-drawer-header, 
#extensions_settings2 .inline-drawer-toggle.inline-drawer-header, 
#user-settings-block h4, 
.standoutHeader{
  background: rgba(19, 47, 76, 0.8);
  color: var(--dark-text-primary);
  border-left: 3px solid var(--dark-accent-primary);
  padding-left: 10px;
}

/* 输入框和文本域 */
.text_pole{
  background: rgba(19, 47, 76, 0.6);
  color: var(--dark-text-primary);
  border: 1px solid var(--dark-border-color);
}

/* 聊天区域背景 */
#chat{
  border:none !important;
  background: var(--dark-bg-primary) !important;
}

body.no-blur #top-bar, body.no-blur #send_form{border:none;}

/* 发送框样式优化 */
#send_textarea{
  background: rgba(26, 35, 50, 0.8);
  border-radius:8px;
  field-sizing:unset;
  height:32px !important;
  border: 1px solid var(--dark-border-color);
  color: var(--dark-text-primary);
  transition: all 0.2s ease;
}
#send_textarea:focus{
  border-color: var(--dark-accent-primary);
  box-shadow: 0 0 0 2px rgba(127, 219, 202, 0.2);
}

/* 图标样式 */
#top-settings-holder .drawer-icon:before{font-size:24px}
#top-settings-holder .openIcon:before,
#top-settings-holder .openIcon:hover:before{color: var(--dark-accent-secondary)}
#nonQRFormItems .fa-solid::before,
#nonQRFormItems .fa-regular::before{font-size:24px;color: var(--dark-text-secondary)}
#send_but:before{color: var(--dark-accent-secondary) !important}

/* 代码块样式 */
.mes_text pre code, .mes_reasoning pre code {
  background: rgba(0, 16, 28, 0.8);
  color: var(--dark-text-primary);
  border-radius: 6px;
  padding: 0.3em 0.5em;
  border: 1px solid var(--dark-border-color);
}

/* 快速回复按钮 */
#qr--bar > .qr--buttons .qr--button:hover, 
#qr--popout > .qr--body > .qr--buttons .qr--button:hover{
  background: rgba(127, 219, 202, 0.2) !important;
  border-color: var(--dark-accent-primary) !important;
}

/* 消息按钮样式 */
.mes_button, .extraMesButtons>div {
    filter: unset;
    opacity: 0.8;
    transition: all 0.2s ease;
}
.mes_button:hover, .extraMesButtons>div:hover {
    opacity: 1;
    transform: translateY(-1px);
}

/* Select2样式 */
span.select2.select2-container .select2-selection__choice__remove,
.select2_choice_clickable_buttonstyle+span.select2-container .select2-selection__choice__display{
  background: rgba(19, 47, 76, 0.6);
  color: var(--dark-text-primary);
  border-color: var(--dark-border-color);
}

/* 提示管理器样式 */
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name,
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt_disabled .completion_prompt_manager_prompt_name .prompt-manager-inspect-action{
  color: var(--dark-text-secondary);
  opacity: 0.6;
}
#completion_prompt_manager .completion_prompt_manager_header_advanced span {
  filter: none;
  color: var(--dark-text-primary);
}
#completion_prompt_manager #completion_prompt_manager_list .completion_prompt_manager_prompt .completion_prompt_manager_prompt_name .fa-solid{
  color: var(--dark-accent-primary);
}
.completion_prompt_manager_list_head span{color: var(--dark-text-primary) !important}
.completion_prompt_manager_header div+div{color: var(--dark-text-primary)}
