  --glow-inner: rgba(42,168,255,0.14);
  --edge-focus: #254569;
  --edge-default: #223650;
  --highlight: #2aa8ff;
  --highlight-mid: rgba(42,168,255,0.35);
  --highlight-dim: rgba(42,168,255,0.12);
  --bevel: 10px;
}

/* 主容器 */
.stellar-wrapper{
  display:flex; justify-content:center; width:100%;
  padding: 0 12px; margin: 12px auto;
  font-family: -apple-system, 'SF Pro Display', BlinkMacSystemFont, 'Helvetica Neue', 'Segoe UI', Roboto, Arial, sans-serif;
}

/* 主面板 */
.stellar-panel{
  width: min(980px, 96vw);
  background: var(--deep-space);
  color: var(--ink-primary);
  border: 1px solid var(--edge-subtle);
  box-shadow: 0 14px 38px rgba(0,0,0,0.55), inset 0 0 0 1px rgba(255,255,255,0.03);
  position: relative; overflow: hidden;
  clip-path: polygon(
    0 var(--bevel), var(--bevel) 0,
    calc(100% - var(--bevel)) 0, 100% var(--bevel),
    100% calc(100% - var(--bevel)), calc(100% - var(--bevel)) 100%,
    var(--bevel) 100%, 0 calc(100% - var(--bevel))
  );
}
.stellar-panel::before{
  content:""; position:absolute; inset:0; pointer-events:none;
  box-shadow: inset 0 0 0 1px rgba(42,168,255,0.06);
  clip-path: inherit;
}
/* 顶部柔和光晕 */
.stellar-panel::after{
  content:""; position:absolute; inset:-1px -1px 60% -1px;
