<script>
(function() {
  const SELECTOR = {
    wrapper: '.stellar-wrapper.stellar-pending',
    panel: '.stellar-panel',
    grid: '.options-grid',
    footer: '.action-footer',
    hint: '.hint-text',
    toggle: '.collapse-hint',
    submit: '.submit-action',
    rawData: '.raw-choices'
  };

  function findTextarea() {
    try {
      const parentDoc = window.parent?.document;
      return parentDoc?.querySelector('#send_textarea') ?? document.querySelector('#send_textarea');
    } catch { return null; }
  }

  function collectSelections(grid) {
    return [...grid.querySelectorAll('.choice-cell.selected')]
      .map(el => el.dataset.value || '')
      .filter(Boolean);
  }

  function readCurrentInput(grid) {
    const textarea = findTextarea();
    const inputVal = textarea?.value?.trim() ?? '';
    return inputVal || collectSelections(grid).join(' ').trim();
  }

  function dispatchMessage(grid) {
    const msg = readCurrentInput(grid);
    if (!msg) return;

    try {
      const slash = window.triggerSlash ?? window.parent?.triggerSlash;
      if (typeof slash === 'function') {
        slash(`/send ${msg}|/trigger`);
      } else {
        const textarea = findTextarea();
        if (textarea) {
